import isEmpty from 'lodash.isempty';

import insiderLib from './index';

export const insiderParamArrayObject = (key, value, dataType) => ({
  key,
  value,
  dataType,
});

export const insiderTrackEvent = ({ window, eventName, params }) => {
  if (isEmpty(params)) {
    insiderLib.trackingEvent(window, eventName);
  } else {
    insiderLib.trackingEvent(window, eventName, params);
  }
};

export const insiderReinitOnWeb = window => {
  insiderLib.init(window);
};
